  req.requestId = fnv.hash(new Date().valueOf() + ipAddress, 128).str();

  sails.models.requestlog.create({
    id: req.requestId,
    ipAddress: ipAddress,
    url: sanitizeRequestUrl(req),
    method: req.method,
    body: _.omit(req.body, 'password'),
